#include <iostream>#include <vector>#include <algorithm>using namespace std;void printVectorInt(vector<int> &v){    vector<int>::iterator it=v.begin();    for(;it!=v.end();it++)    {        cout<<*it<<" ";    }    cout<<endl;}void test02(){    vector<int> v1;    v1.push_back(10);    v1.push_back(50);    v1.push_back(30);    v1.push_back(90);    v1.push_back(70);    printVectorInt(v1);    sort(v1.begin(), v1.end(), greater<int>());    printVectorInt(v1);}void test03(){    vector<int> v1;    v1.push_back(10);    v1.push_back(50);    v1.push_back(30);    v1.push_back(90);    v1.push_back(70);    vector<int>::iterator  ret;    ret = find_if(v1.begin(), v1.end(), bind2nd(greater<int>(),30));    if(ret != v1.end())    {        cout<<*ret<<endl;    }}int main(){	test02();	return 0;}