#include <iostream>#include <vector>#include <algorithm>using namespace std;//第二步:公共继承binary_function 参数萃取class PrintInt:public binary_function<int,int,void>{public:    //第三步:const修饰operator()    void operator()(int val, int tmp) const    {        //cout<<val+tmp<<" ";        cout<<"val ="<<val<<" tmp="<<tmp<<endl;    }};int main(){    vector<int> v1;    v1.push_back(10);    v1.push_back(50);    v1.push_back(30);    v1.push_back(90);    v1.push_back(70);    //第一步:bind2nd或bind1st绑定参数    for_each(v1.begin(), v1.end(), bind2nd(PrintInt(), 200));    //for_each(v1.begin(), v1.end(), bind1st(PrintInt(), 200));    cout<<endl;	return 0;}