#include <iostream>#include <vector>#include <algorithm>using namespace std;void printVectorInt(vector<int> &v){    vector<int>::iterator it=v.begin();    for(;it!=v.end();it++)    {        cout<<*it<<" ";    }    cout<<endl;}void test09(){    vector<int> v1;    v1.push_back(10);    v1.push_back(30);    v1.push_back(50);    vector<int> v2;    v2.push_back(20);    v2.push_back(40);    v2.push_back(60);    v2.push_back(80);    vector<int> v3;    v3.resize(v1.size()+v2.size());    merge(v1.begin(), v1.end(), v2.begin(),v2.end(), v3.begin());    printVectorInt(v3);}#include<time.h>void test10(){    //设置随机数种子    srand(time(NULL));    vector<int> v1;    v1.push_back(10);    v1.push_back(20);    v1.push_back(30);    v1.push_back(40);    v1.push_back(50);    printVectorInt(v1);    random_shuffle(v1.begin(), v1.end());    printVectorInt(v1);}void test11(){    vector<int> v1;    v1.push_back(10);    v1.push_back(20);    v1.push_back(30);    v1.push_back(40);    v1.push_back(50);    printVectorInt(v1);    reverse(v1.begin(), v1.end());    printVectorInt(v1);}int main(){	test09();	return 0;}